select xx.zsxm_dm zsxm_dm, xx.zsxmmc sz, a.bq_1 ysb, nvl(b.tqtc_2,0) qcysb , nvl(c.qntq_ysb_yc,0) ysbqyycxsy, nvl(d.qntq_fysb_yc,0) fysbqyycxsy, nvl(e.tqtc_5,0) qntqsbs, nvl((a.bq_1+nvl(b.tqtc_2,0)-nvl(d.qntq_fysb_yc,0)),0) ycck_a, nvl((nvl(e.tqtc_5,0)-nvl(c.qntq_ysb_yc,0)-nvl(d.qntq_fysb_yc,0)),0) ycck_b from (select t.id_zsxm, nvl(sum(t.sk_sb), 0) bq_1 from a_taf_nsrysb t where t.dt_ysb >=to_date( ? ,'yyyy-mm-dd') and t.dt_ysb <= to_date( ? ,'yyyy-mm-dd') and t.zgswskfj_dm like ? group by t.id_zsxm) a, (select sbxx.zsxm_dm,nvl(sum(sbxx.jsyj),0) as tqtc_2 from Sb_Sbxx sbxx where sbxx.sbqx>=add_months(to_date( ? ,'yyyy-mm-dd'),-12) and sbxx.sbqx<=add_months(to_date( ? ,'yyyy-mm-dd'),-12) and sbxx.gzlx_dm_1 in ('1','5') and sbxx.zfbz_1<>'Y' and Sbxx.zsxm_dm <>'10112' and sbxx.zgswskfj_dm like ? and not exists( select 1 from a_gc_ysbqymd ysbqy where ysbqy.djxh = sbxx.djxh and ysbqy.yxbj='Y' and ysbqy.yyqq<= to_date( ? ,'yyyy-mm-dd') and ysbqy.yyqz>=to_date( ? ,'yyyy-mm-dd') ) group by sbxx.zsxm_dm union all select sbxx.zsxm_dm,nvl(sum(sbxx.ynse),0) as tqtc_2 from Sb_Sbxx sbxx where sbxx.sbqx>=add_months(to_date( ? ,'yyyy-mm-dd'),-12) and sbxx.sbqx<=add_months(to_date( ? ,'yyyy-mm-dd'),-12) and sbxx.gzlx_dm_1 in ('1','5') and sbxx.zfbz_1<>'Y' and Sbxx.zsxm_dm ='10112' and sbxx.zgswskfj_dm like ? and not exists( select 1 from a_gc_ysbqymd ysbqy where ysbqy.djxh = sbxx.djxh and ysbqy.yxbj='Y' and ysbqy.yyqq<= to_date( ? ,'yyyy-mm-dd') and ysbqy.yyqz>=to_date( ? ,'yyyy-mm-dd') ) group by sbxx.zsxm_dm ) b , ( select ysb.zsxm_dm, (sum(ysb.ycxsyje) ) as qntq_ysb_yc from a_Gc_Ycxsydy ysb where ysb.yxbj='Y' and ysb.nssbrq>=add_months(to_date( ? ,'yyyy-mm-dd'),-12) and ysb.nssbrq<=add_months(to_date( ? ,'yyyy-mm-dd'),-12) and exists ( select 1 from a_gc_ysbqymd ysbqy where ysbqy.djxh = ysb.djxh and ysbqy.yxbj='Y' and ysbqy.yyqq<= to_date( ? ,'yyyy-mm-dd') and ysbqy.yyqz>=to_date( ? ,'yyyy-mm-dd') ) and ysb.zgswskfj_dm like ? group by ysb.zsxm_dm
) c, ( select ysb.zsxm_dm, (sum(ysb.ycxsyje) ) as qntq_fysb_yc from a_Gc_Ycxsydy ysb where ysb.yxbj='Y' and ysb.nssbrq>=add_months(to_date( ? ,'yyyy-mm-dd'),-12) and ysb.nssbrq<=add_months(to_date( ? ,'yyyy-mm-dd'),-12) and exists ( select 1 from a_gc_ysbqymd ysbqy where ysbqy.djxh = ysb.djxh and ysbqy.yxbj='Y' and ysbqy.yyqq<= to_date( ? ,'yyyy-mm-dd') and ysbqy.yyqz>=to_date( ? ,'yyyy-mm-dd') ) and ysb.zgswskfj_dm like ? group by ysb.zsxm_dm
) d, (select sbxx.zsxm_dm,nvl(sum(sbxx.jsyj),0) as tqtc_5 from Sb_Sbxx sbxx where sbxx.sbqx>=add_months(to_date( ? ,'yyyy-mm-dd'),-12) and sbxx.sbqx<=add_months(to_date( ? ,'yyyy-mm-dd'),-12) and sbxx.gzlx_dm_1 in ('1','5') and sbxx.zfbz_1<>'Y' and Sbxx.zsxm_dm <>'10112' and sbxx.zgswskfj_dm like ? group by sbxx.zsxm_dm union all select sbxx.zsxm_dm,nvl(sum(sbxx.ynse),0) as tqtc_5 from Sb_Sbxx sbxx where sbxx.sbqx>=add_months(to_date( ? ,'yyyy-mm-dd'),-12) and sbxx.sbqx<=add_months(to_date( ? ,'yyyy-mm-dd'),-12) and sbxx.gzlx_dm_1 in ('1','5') and sbxx.zfbz_1<>'Y' and Sbxx.zsxm_dm ='10112' and sbxx.zgswskfj_dm like ? group by sbxx.zsxm_dm ) e , (select zsxm.zsxm_dm, zsxm.zsxmmc from dm_gy_zsxm zsxm order by zsxm.zsxm_dm) xx where xx.zsxm_dm = a.id_zsxm and xx.zsxm_dm = b.zsxm_dm(+) and xx.zsxm_dm = c.zsxm_dm(+) and xx.zsxm_dm = d.zsxm_dm(+) and xx.zsxm_dm = e.zsxm_dm(+)
group by xx.zsxm_dm, xx.zsxmmc, a.bq_1,b.tqtc_2,c.qntq_ysb_yc,d.qntq_fysb_yc,e.tqtc_5 order by xx.zsxm_dm
